import{_ as o,c as i,b as t,e as s,h as r,w as a,i as l,j as p,a as d}from"./app-BPzQcCmO.js";const m="/assets/img/No-fighting/No-fighting.jpg",u={};function f(g,e){const n=p("font");return d(),i("div",null,[e[4]||(e[4]=t("link",{rel:"stylesheet",href:"/css/font-style.css"},null,-1)),e[5]||(e[5]=t("br",null,null,-1)),e[6]||(e[6]=t("br",null,null,-1)),s(n,{class:"gradient-title-center"},{default:a(()=>e[0]||(e[0]=[t("p",null,"“我们的思想是块泥土，随着时日的变迁，它被揉捏成不同的形状——我们休息的时候，那是好的，而一上火线，它就死了，里里外外都是一片片弹坑。”",-1)])),_:1,__:[0]}),s(n,{style:{"text-align":"right"}},{default:a(()=>e[1]||(e[1]=[t("p",null,"—— qwp 2025.6.30",-1)])),_:1,__:[1]}),e[7]||(e[7]=r('<h2 id="我们生活在水深火热中-事前记" tabindex="-1"><a class="header-anchor" href="#我们生活在水深火热中-事前记"><span>我们生活在水深火热中：事前记</span></a></h2><p> 这本书既不是一种控诉，也不是一份自白。它只是试图叙述那样一代人，他们即使逃过了炮弹，也还是被战争毁灭了。对我们这些十八岁的小伙子而言，他们理应成为我们走向成熟的世界，走向工作、职责、文化和进步的世界，走向未来的中间人和向导。我们常常嘲笑他们，稍稍作弄他们一下，但是在内心深处却是信任他们的。他们代表的权威的观念，在我们思想中是和更远大的见识、更合乎人性的智慧联系在一起的。可是我们看到的第一个死者，却把这种信念完全击碎了。我们不能不认识到我们这一代要比他们那一代更加正直，他们只是在空谈和圆滑方面超过了我们。那第一次密集的炮火就向我们揭示出了我们的错误，而他们教给我们的那种世界观也在炮火底下崩溃了。他们继续写的文章，发表着的演说...而我们却已经看到了野战医院和死亡；他们依旧在说对国家的责任是头等大事，而我们却已经知道死亡的剧痛比以前更为强烈了。尽管这样，我们绝不做叛乱者，绝不做逃兵，也绝不做懦夫...</p><p> 事实上，战争的故事可以写的轰轰烈烈，它可以把一个人完全塑造成英雄或者彻头彻尾的懦夫...但是它没有，我们只看到了无数的死伤和来自灵魂摧残。我们已经不再是青年。我们不愿再对这个世界发动进攻。我们是“逃兵”。我们躲开自己，躲开我们的生活。我们才十八岁，刚刚开始热爱这个世界，热爱生活，然而我们不得不对它开炮。那第一颗打来的榴弹，击中了我们的心坎。我们与行动、追求和进步割断了联系。我们再也不相信它们了。我们相信战争。战时的军队制度扼杀了我们的个性和思想，而一颗擦亮的纽扣要远比四卷本的叔本华更重要！</p><p> 是的，我们像旅人般漫游在年少时的风景中。我们被事实焚毁，像商人般懂得辨识，像屠夫般清楚利害。我们不再无忧无虑，我们的冷漠令人生畏。我们乐意徜徉其中，但我们能活着吗？我们像被遗弃的孩子，像老道的年长者。粗暴、悲伤、浅薄们迷失了。前线、后备、炊事班、医院或者是返乡探亲...好吧，事实上我不知道我应该去哪里，每一个地方都像悬崖下的地刺刺激着我的神经，迷茫和不知所措使我忘记我究竟来自哪里...尽管这在战场上是大忌。我该拖着身体去看看我的战友们了，最好是打上几轮牌，忘记一些事情...</p><h2 id="斟满一杯杯的酒-他们" tabindex="-1"><a class="header-anchor" href="#斟满一杯杯的酒-他们"><span>斟满一杯杯的酒：他们</span></a></h2><p> 还乡是前线士兵都期待的事，没有人会厌恶，甚至愿意以受伤为代价...但真的到返乡时，一股莫名的情感上升。这真的是我的故乡么？那时候我的母亲还没有患癌，我的父亲也还没有满头白发，街道上也偶尔传来小孩子们玩耍的声音...到底是物是人非，可为什么我还是感到陌生？明明我已来到我的故里？</p><p> 当我从门口进入家中，来不及放下行李和头盔，傻傻的站着，看着这个曾经我生活的地方，不知为什么，到底是放不下沉重的包裹。我看到我的书房，那些没来得及还的和不愿还得书、我的沙发和我的儿时的衣服，一切都那么突然、那么虚幻又那么真实...</p><p> 后来的几天，我开始随着家乡到处游走。校长热情的招待我，夸夸其谈他对战事的见解、酒吧老板为我斟满酒杯，希望我透露前线的战事、父亲引以为傲，希望我不要脱下身上那身肮脏的军装...而我仅仅只是默默的看着，或偶尔蹦出几个文字，我开始感到枯燥和厌恶，到底是什么改变了？我不明白，我开始觉得那些罗嗦的话语变得无足轻重，变得毫无价值，前线的战友还在死守，而后方的人们却在喝着啤酒，畅快的谈论我们怎么赢得胜利，什么时候赢得胜利...这就是他们口中的战争。我想我该去到我该去的地方了...</p><h2 id="一双完好如新的靴子-我们" tabindex="-1"><a class="header-anchor" href="#一双完好如新的靴子-我们"><span>一双完好如新的靴子：我们</span></a></h2><p> 和战友呆在一起，是我们求之不得的。我无法想象一个我融入不了的生活，失去战友，失去工作，没有恋人...当我们还在的时候，就经常坐在一起谈论什么时候战争结束？结束之后该干什么？他们每个人都有自己的想法，但是他们都会或多或少的对前途感到迷茫。“我们能活着么？”，“什么时候能结束战争？”，“什么时候才能看到我的家人？”...我想我们应该彼此祝福，最好是站在战争中的战壕下。当炮弹从我们头顶经过时，我们会产生破碎感，这种感觉会让我们迷失，让我们失去理智不顾危险向前冲锋，哪怕下一秒丢失性命，但这也是我们唯一活下去的做法。杀掉他们，我们的敌人！</p><p> 克默里希死了，他的靴子后来给了米勒...但这双靴子没能带给他或者我们好运，我们没能挺过这场战争......</p><h2 id="共同的家人-你们" tabindex="-1"><a class="header-anchor" href="#共同的家人-你们"><span>共同的家人：你们</span></a></h2><p> 抱歉，我的敌人。我不想伤害任何一个人，这种矛盾是不可避免的，我希望你能理解...当我趴在战壕中时，就已经做好有人跳进来，刺向我的准备，我必须在你之前杀掉你。这并非仇恨，只是生存的本能，像两只被逼到墙角的困兽，獠牙相向并非为了征服，只为多喘一口气。我看见过你的脸，在硝烟的间隙，在望远镜的十字线里，或者更近——就在我刺刀捅入的那个瞬间。一张年轻的脸，或许比我更年轻，沾满了污泥和惊恐，眼睛里映着和我一样的、被巨大恐惧攫住的空洞。那一刻，我刺穿的仿佛不是一个“敌人”，而是另一个被塞进陌生制服里的、瑟瑟发抖的“我”。</p><p> 我们本该是共同的家人，拥有同样的日升月落，同样的面包香气，同样在炉火边听过的故事。你的母亲是否也像我的母亲一样，在某个遥远的地方，手指无意识地摩挲着相框的边缘，日夜祈祷？你的父亲是否也曾在某个晴朗的下午，教你识别田间的作物？我们本该分享这些平凡而珍贵的瞬间，而不是在这片被铁与血浸泡透了的烂泥地里，为了一个我们都不明白、也无法掌控的理由，互相把对方变成冰冷的尸体和破碎的梦。</p><p> 你倒下了，眼睛还睁着，望向这片不属于任何人的天空。我翻找你的口袋，不是为了战利品，也许只是想找到一点证明，证明你和我一样，也曾是个有血有肉、有牵挂的人。一张皱巴巴的照片掉了出来，上面是你和一个姑娘，还有一个襁褓中的婴儿，在阳光下笑着。那笑容如此刺眼，像一颗烧红的子弹打在我的心上。这就是我杀死的“敌人”？一个丈夫？一个父亲？一个和我一样，只想活着回家的人？我们本该是共同的家人啊！为什么？为什么我们要在这里，用彼此的生命去填满那些将军们的地图和政客们的演说词？这该死的战争机器，碾碎了所有属于“人”的东西，只留下编号、制服和无法消弭的、深入骨髓的罪疚感。我们，我和你，都是这巨大绞肉机里微不足道的碎屑。</p><h2 id="送葬人-是谁杀死了我们" tabindex="-1"><a class="header-anchor" href="#送葬人-是谁杀死了我们"><span>送葬人：是谁杀死了我们？</span></a></h2><p> 卡特也死了，这是最后一个...那枚该死的弹片，像命运开的一个卑劣玩笑，在他以为终于可以喘口气，在停战似乎近在咫尺的时候，夺走了他。我抱着他，感受着他生命的温度一点点流逝，就像抱着米勒，抱着克默里希，抱着所有那些我曾称之为战友的人。他的血浸透了我的前襟，温热黏腻，是生命最后的告别。</p><p> 我是谁？保罗·博伊默尔？一个名字，一个编号。我是整个二连，不，是整个前线唯一剩下的幽灵。我的世界，那个由克托、阿尔贝特、米勒、克默里希、莱尔、卡特...他们共同构成的、在恐惧和污秽中依然闪烁着人性微光的世界，彻底崩塌了。我环顾四周，只有空荡荡的战壕，沉默的废墟，和呼啸的风声。家？那个我返乡时感到如此陌生和疏离的地方？它已遥不可及，像一个褪色的旧梦。前线？这里只剩下尸体和等待腐烂的寂静。哪里能容得下我？一个被战争从里到外彻底改造过的人，一个灵魂早已布满比大地更深更密弹坑的躯壳？我成了自己生命的送葬人，亲手埋葬了青春、希望、所有我爱的人和爱我的人。</p>',18)),s(n,{class:"strong"},{default:a(()=>e[2]||(e[2]=[l(" 是谁杀死了我们？")])),_:1,__:[2]}),e[8]||(e[8]=t("p",null," 是呼啸的炮弹和嘶鸣的子弹吗？是那些在泥泞中跃起的灰色身影吗？不。它们只是工具，是执行者。",-1)),e[9]||(e[9]=t("p",null," 是那些在后方夸夸其谈的校长、酒吧老板、挥舞着报纸的爱国者吗？是那些用空洞的“责任”、“荣誉”、“国家”为我们编织裹尸布的人吗？他们点燃了火，却躲在安全的距离外欣赏火焰的“壮丽”。",-1)),e[10]||(e[10]=t("p",null," 是那个庞大、冰冷、吞噬一切的战争机器本身吗？它碾碎了思想，扼杀了个性，把活生生的人变成会呼吸的武器零件，把“纽扣擦亮”的意义置于哲学与生命之上。",-1)),e[11]||(e[11]=t("p",null," 也许，是我们十八岁时对权威那盲目的信任？是我们曾经相信的那个关于世界、关于进步、关于成人智慧的谎言？当第一颗炮弹落下，那个谎言就和第一个倒下的战友一样，粉身碎骨了。",-1)),e[12]||(e[12]=t("p",null," 我们死于一个时代狂妄的野心，死于远离战场者的冷漠与狂热，死于被精心培育又被无情背叛的幻梦。我们死于被教导要去热爱，却又被迫亲手摧毁的那个世界。我们死于战争——这场由无数双手共同推动、最终却让最年轻的生命在泥泞中独自咽下苦果的巨大荒谬。",-1)),s(n,{class:"strong"},{default:a(()=>e[3]||(e[3]=[l("  西线无战事。 "),t("p",null," 报告上是这样写的。",-1)])),_:1,__:[3]}),e[13]||(e[13]=t("p",null,[l(" 1918年10月，保罗·博伊默尔阵亡。那一天整个前线是如此平静和沉寂，以至于军队指挥部的报告上仅仅写着这样一句话："),t("strong",null,"西线无战事"),l("。"),t("br"),l("  他向前倒下了，像是趴在地上睡着了。如果把他翻过来，会看见他脸上没有痛苦，只有平静，仿佛几乎对就这样结束感到满意。"),t("br"),l("  而我，那个曾经叫保罗的灵魂，漂浮在这片死寂之上。没有控诉，没有答案，只有无边无际的虚无和那句冰冷的、最终的判决："),t("strong",null,"西线无战事"),l("。战争结束了它的咀嚼，吐出最后一块残渣，归于沉寂。而我们，一代人，就这样永远地迷失了。")],-1)),e[14]||(e[14]=t("p",null,[t("img",{src:m,style:{zoom:"25%",display:"block",margin:"0 auto"}})],-1)),e[15]||(e[15]=t("p",null,[l(" 部分参考："),t("a",{href:"https://www.bilibili.com/video/BV1g7Kyz3Emc/?spm_id_from=333.337.search-card.all.click&vd_source=dca5deabbb49f895ff5ec660b9e33dd4",target:"_blank",rel:"noopener noreferrer"},"行路漫漫")],-1))])}const b=o(u,[["render",f]]),k=JSON.parse('{"path":"/posts/BookReview/No-fighting.html","title":"让那些喜欢流血的人去流血吧!","lang":"en-US","frontmatter":{"title":"让那些喜欢流血的人去流血吧!","description":"西线无战事","date":"2025-07-01T00:00:00.000Z","timeline":true,"head":[["meta",{"name":"keywords","content":"反战、西线无战事、让那些喜欢流血的人去流血吧!"}],["meta",{"name":"description","content":"这是被毁掉的一代人"}],["meta",{"name":"author","content":"RobinElysia"}]],"category":["RobinElysia"],"tag":["书评"]},"headers":[{"level":2,"title":"我们生活在水深火热中：事前记","slug":"我们生活在水深火热中-事前记","link":"#我们生活在水深火热中-事前记","children":[]},{"level":2,"title":"斟满一杯杯的酒：他们","slug":"斟满一杯杯的酒-他们","link":"#斟满一杯杯的酒-他们","children":[]},{"level":2,"title":"一双完好如新的靴子：我们","slug":"一双完好如新的靴子-我们","link":"#一双完好如新的靴子-我们","children":[]},{"level":2,"title":"共同的家人：你们","slug":"共同的家人-你们","link":"#共同的家人-你们","children":[]},{"level":2,"title":"送葬人：是谁杀死了我们？","slug":"送葬人-是谁杀死了我们","link":"#送葬人-是谁杀死了我们","children":[]}],"git":{"updatedTime":1762519389000,"contributors":[{"name":"qwp_p","username":"","email":"qwp20060309@outlook.com","commits":2}],"changelog":[{"hash":"cfd09171d1be97fa09ef8c4fc2be80d788219c63","time":1762519389000,"email":"qwp20060309@outlook.com","author":"qwp_p","message":"Update blog content and add new articles"},{"hash":"4c644f83f2c04e7b866c269634041fbc3addef9f","time":1755331266000,"email":"qwp20060309@outlook.com","author":"qwp_p","message":"博客更改"}]},"filePathRelative":"posts/BookReview/No-fighting.md","excerpt":"<link rel=\\"stylesheet\\" href=\\"/css/font-style.css\\">\\n<br>\\n<br>\\n\\n\\n<h2>我们生活在水深火热中：事前记</h2>\\n<p> 这本书既不是一种控诉，也不是一份自白。它只是试图叙述那样一代人，他们即使逃过了炮弹，也还是被战争毁灭了。对我们这些十八岁的小伙子而言，他们理应成为我们走向成熟的世界，走向工作、职责、文化和进步的世界，走向未来的中间人和向导。我们常常嘲笑他们，稍稍作弄他们一下，但是在内心深处却是信任他们的。他们代表的权威的观念，在我们思想中是和更远大的见识、更合乎人性的智慧联系在一起的。可是我们看到的第一个死者，却把这种信念完全击碎了。我们不能不认识到我们这一代要比他们那一代更加正直，他们只是在空谈和圆滑方面超过了我们。那第一次密集的炮火就向我们揭示出了我们的错误，而他们教给我们的那种世界观也在炮火底下崩溃了。他们继续写的文章，发表着的演说...而我们却已经看到了野战医院和死亡；他们依旧在说对国家的责任是头等大事，而我们却已经知道死亡的剧痛比以前更为强烈了。尽管这样，我们绝不做叛乱者，绝不做逃兵，也绝不做懦夫...</p>"}');export{b as comp,k as data};
