import{_ as o,c as s,b as t,i as n,e as a,w as r,j as i,a as d}from"./app-DFfsekp4.js";const m={};function p(u,e){const l=i("font");return d(),s("div",null,[e[6]||(e[6]=t("link",{rel:"stylesheet",href:"/css/font-style.css"},null,-1)),e[7]||(e[7]=t("h2",{id:"看不到的明天",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#看不到的明天"},[t("span",null,"看不到的明天")])],-1)),t("p",null,[e[1]||(e[1]=n(" 第一次读加缪的书，而且还是他的第一部成名之作——《局外人》。这本书给人的第一印象很深刻，首先能想到的是主人公可能是一个置身事外的人、不受任何拘束的人。想法是正确的，但在加缪的笔下，这种想法可能有些许单调。书里有这样一句话：“进退两难，出路是没有的。”如果说依照公认的精神准则运作的人类社会是一个局的话，个体要么入局，遵循游戏规则；要么作为异类被规则审判，最终投向绞刑架。")),e[2]||(e[2]=t("br",null,null,-1)),e[3]||(e[3]=n("  主角默尔索就是这样一个异类。他习惯缄默，因为“没什么可说的，于是我就不说话”；他不关心身边的人，甚至不记得母亲多大岁数；他觉得很多事情毫无意义，面对女友“爱不爱我”的问题连谎话都不愿意吐露；他不愿意改变生活，因为“生活是无法改变的，什么样的生活都一样”；他关注肉体需求大于精神，“肉体上的需要常常使我的感情混乱”。因此他在母亲葬礼上感受到的不是失亲的悲伤，而是天气的炎热、身体的疲惫，因此在葬礼第二天就与情人约会，去海滨浴场有用，去看喜剧电影。他开枪杀人却毫无动机，只是因为海滩上的灼热而不由自主扳动枪机。他被投进监狱，引起感官直觉的更多是无关紧要的自然景色，而对帮助他的律师、推事和神甫视若无睹。")),e[4]||(e[4]=t("br",null,null,-1)),e[5]||(e[5]=n("  这种人在现在的社会看来甚至是偏激的，甚至在对生活的积极态度上比尼采还要消极，既没有对美好生活的渴望、也没有对现状的改变。但是真的是这样么？加缪曾经谈起过自己笔下的默尔索，他声称")),a(l,{class:"gradient-title"},{default:r(()=>e[0]||(e[0]=[n("“默尔索‘远非麻木不仁，他怀有一种执着而深沉的激情，对于绝对和真实的激情。’他早已洞悉这个世界的荒谬，‘我不知道’、‘毫无意义’两句话被他悬挂嘴边，‘厌烦’则是他面对人事时的常态。默尔索意识到世界没有意义，没有出路，认识到世界对于人的种种欲望漠不关心，认识到人同世界特别是人同社会这种不协调乃至对立的关系。只不过他没有像柏拉图那样认为世界万物是‘理念’的影子，那样过于虚幻；也不认同禅宗的生命‘如露如电’，从而寻求涅槃寂静；更没有鲁滨逊的‘经济人意识’，唯利是图。他热爱自然，渴慕自由，珍惜每分每秒，完全靠着自己现实的理性与实践精神支配着一切行动。”")])),_:1,__:[0]})]),e[8]||(e[8]=t("h2",{id:"把书扔到喷泉中去",tabindex:"-1"},[t("a",{class:"header-anchor",href:"#把书扔到喷泉中去"},[t("span",null,"把书扔到喷泉中去")])],-1)),e[9]||(e[9]=t("p",null,[n(" 缄默同时也是一种回答，有时候或许抛弃那些繁杂的步骤，我们才能看到本质，就像默尔索一样。但我并不觉得这是一种可行的、可取的方法，这本书不仅仅说明了主角默尔索的超脱，更是反映了社会的激化与矛盾，我们无法完全像默尔索一样，或者说我们不应该在看透人事后变得消极等待。从书中走出来，走到建设自己中去，像尼采的一样认识到荒诞和虚无，更要克服它、超越它，通过创造新的价值赋予生命以意义。或者像鲁米一样扔掉他的书到喷泉中，走到属于我们自己的中心。"),t("br"),n("  也许从加缪的“荒谬”哲学角度看，默尔索是更加自由和理性的；而回归现实，他只能走向死亡。")],-1))])}const b=o(m,[["render",p]]),c=JSON.parse('{"path":"/posts/BookReview/%E7%BC%84%E9%BB%98%E8%80%85.html","title":"缄默者","lang":"en-US","frontmatter":{"title":"缄默者","description":"缄默者","date":"2025-08-16T00:00:00.000Z","timeline":true,"head":[["meta",{"name":"keywords","content":"局外人、缄默者、关于我"}],["meta",{"name":"description","content":"缄默是一种超越世界的方式，我们在这里看到了真正的我们"}],["meta",{"name":"author","content":"RobinElysia"}]],"category":["RobinElysia"],"tag":["书评"]},"headers":[{"level":2,"title":"看不到的明天","slug":"看不到的明天","link":"#看不到的明天","children":[]},{"level":2,"title":"把书扔到喷泉中去","slug":"把书扔到喷泉中去","link":"#把书扔到喷泉中去","children":[]}],"git":{"updatedTime":1762519389000,"contributors":[{"name":"qwp_p","username":"","email":"qwp20060309@outlook.com","commits":2}],"changelog":[{"hash":"cfd09171d1be97fa09ef8c4fc2be80d788219c63","time":1762519389000,"email":"qwp20060309@outlook.com","author":"qwp_p","message":"Update blog content and add new articles"},{"hash":"895863e6a6db72cd2f0e8586589c8c0338fa401f","time":1755332073000,"email":"qwp20060309@outlook.com","author":"qwp_p","message":"docs: 完善 README 文档"}]},"filePathRelative":"posts/BookReview/缄默者.md","excerpt":"<link rel=\\"stylesheet\\" href=\\"/css/font-style.css\\">\\n<h2>看不到的明天</h2>\\n<p> 第一次读加缪的书，而且还是他的第一部成名之作——《局外人》。这本书给人的第一印象很深刻，首先能想到的是主人公可能是一个置身事外的人、不受任何拘束的人。想法是正确的，但在加缪的笔下，这种想法可能有些许单调。书里有这样一句话：“进退两难，出路是没有的。”如果说依照公认的精神准则运作的人类社会是一个局的话，个体要么入局，遵循游戏规则；要么作为异类被规则审判，最终投向绞刑架。<br>\\n 主角默尔索就是这样一个异类。他习惯缄默，因为“没什么可说的，于是我就不说话”；他不关心身边的人，甚至不记得母亲多大岁数；他觉得很多事情毫无意义，面对女友“爱不爱我”的问题连谎话都不愿意吐露；他不愿意改变生活，因为“生活是无法改变的，什么样的生活都一样”；他关注肉体需求大于精神，“肉体上的需要常常使我的感情混乱”。因此他在母亲葬礼上感受到的不是失亲的悲伤，而是天气的炎热、身体的疲惫，因此在葬礼第二天就与情人约会，去海滨浴场有用，去看喜剧电影。他开枪杀人却毫无动机，只是因为海滩上的灼热而不由自主扳动枪机。他被投进监狱，引起感官直觉的更多是无关紧要的自然景色，而对帮助他的律师、推事和神甫视若无睹。<br>\\n 这种人在现在的社会看来甚至是偏激的，甚至在对生活的积极态度上比尼采还要消极，既没有对美好生活的渴望、也没有对现状的改变。但是真的是这样么？加缪曾经谈起过自己笔下的默尔索，他声称</p>"}');export{b as comp,c as data};
